/*
 * this is the hard reset of glcd
 * use of low signal on reset pin of glcd
 * */
void glcd_reset(void)
{
	//GLCD_SELECT();
	GLCD_RESET_LOW();
	HAL_Delay(GLCD_RESET_TIME);
	GLCD_RESET_HIGH();
	//GLCD_DESELECT();
}


////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
void glcd_command(uint8_t c)
{
	GLCD_A0_LOW();
	glcd_spi_write(c);	
}

void glcd_data(uint8_t c)
{
	GLCD_A0_HIGH();
	glcd_spi_write(c);	
}

